mapper.select:
select distinct c.*, d.*, c.img 'attractionImg', c.user_id 'attractionUserId' from record a join attraction_info c on a.attraction_key = c.attraction_key join attraction_type d on c.type_code = d.type_code where a.tape_key = #{tapeKey}
and c.${keyword} like concat('%', #{word},'%')
limit ${start}, ${countPerPage}

select *, c.img 'attractionImg', c.user_id 'attractionUserId' from record a left outer join user b on a.user_id = b.user_id left outer join attraction_info c on a.attraction_key = c.attraction_key left outer join attraction_type d on c.type_code = d.type_code left outer join () e on a.record_key = e.record_key where a.tape_key = #{tapeKey}
and a.${keyword} like concat('%', #{word},'%')
order by a.createtime desc limit ${start}, ${countPerPage}
